import React, { Component } from 'react';

class GameQuestionLogic extends Component {
  constructor(props){
    super(props);

    this.xValue = 0;
    this.yValue = 0;
    this.zValue = 0;
    this.suggestedAnswer = 0;
    this.questionsCount = 0;
    this.correctAnswersCount = 0;

    // run the 
    this.geneateQuestion;

    this.state = {
      x: this.xValue,
      y: this.yValue,
      z: this.zValue,
      sugAnswer: this.suggestedAnswer,
    }
  }

  generateQuestion(){
    this.xValue = Math.floor(Math.random() * 100);
    this.yValue = Math.floor(Math.random() * 100);
    this.zValue = Math.floor(Math.random() * 100);
    this.suggestedAnswer = Math.floor(Math.random() * 3) + this.xValue + this.yValue + this.zVale;
  }

  render(){
    return(
      <div className="game">
          <h2>Mental Math</h2>
          <div className="equation">
            <p className="text">{`${this.state.x} + ${this.state.y} + ${this.state.z} = ${this.state.sugAnswer}`}</p>
          </div>
          <button>True</button>
          <button>False</button>
          <p className="text">
            Your Score: {numCorrect}/{numQuestions}
          </p>
      </div>
    )
  }
}

export default GameQuestionLogic;
