import React, { Component } from 'react';
import GameResults from './GameResults';

class GameQuestionLogic extends Component {
  constructor(props){
    super(props);

    this.xValue = 0;
    this.yValue = 0;
    this.zValue = 0;
    this.systemAnswer = 0;
    this.questionsCount = 0;
    this.correctAnswersCount = 0;

    // run the function on app load
    this.generateQuestion();

    // binding of events
    this.handleOnClickOfTrueButton = this.handleOnClickOfTrueButton.bind(this);
    this.handleOnClickOfFalseButton = this.handleOnClickOfFalseButton.bind(this);

    this.state = {
      x: this.xValue,
      y: this.yValue,
      z: this.zValue,
      sysAnswer: this.systemAnswer,
      questionsCount: this.questionsCount,
      answersCount: this.correctAnswersCount
    }
  }

  // generate questions and set state values
  generateQuestion = () => {
    this.xValue = Math.floor(Math.random() * 100);
    this.yValue = Math.floor(Math.random() * 100);
    this.zValue = Math.floor(Math.random() * 100);
    this.systemAnswer = Math.floor(Math.random() * 3) + this.xValue + this.yValue + this.zValue;

    console.log('Gen Question', `x: ${this.xValue}, y: ${this.yValue}, z: ${this.zValue}, ans: ${this.systemAnswer}`);
    //return '';
  }

  updateGameState = () => {
    this.setState((presentState) => ({
      x: this.xValue,
      y: this.yValue,
      z: this.zValue,
      sysAnswer: this.systemAnswer,
    }))
  }

  // check if the answers are correct
  markQuestionForAnswer = (answerFromPlayer) => {
    this.generateQuestion();
    const correctAnswer = this.xValue + this.yValue + this.zValue;
    console.log('Correct Answer', correctAnswer);
    if(correctAnswer === this.systemAnswer && answerFromPlayer === 'true'){
      return correctAnswer;
    } else {
      return false;
    }
  }

  handleCheckAnswerIsCorrect = (correct) => {
    if(correct){
      this.setState((presentState) => ({
        answersCount: presentState.answersCount + 1
      }))
    } else {
      this.setState((presentState) => ({
        questionsCount: presentState.questionsCount + 1
      }))
    }
  }

  // the true button is clicked
  handleOnClickOfTrueButton = (answerTrue) => {
    this.markQuestionForAnswer(answerTrue);
    this.updateGameState();
    this.handleCheckAnswerIsCorrect(answerTrue);
    return '';
  }

  // when the false button is clicked
  handleOnClickOfFalseButton = (answerFalse) => {
    this.markQuestionForAnswer(answerFalse);
    this.handleCheckAnswerIsFalse(answerFalse);
    return '';
  }

  render(){
    return(
      <div className="game">
          <h2>Mental Math</h2>
          <div className="equation">
            <p className="text">{`${this.state.x} + ${this.state.y} + ${this.state.z} = ${this.state.sysAnswer}`}</p>
          </div>
          <button onClick={() => this.handleOnClickOfTrueButton('true')}>True</button>
          <button onClick={() => this.handleOnClickOfFalseButton('false')}>False</button>
          <p className="text">
            <GameResults 
              correctAnswersCount={this.state.answersCount}
              questionsCount={this.state.questionsCount}
            />
          </p>
      </div>
    )
  }
}

export default GameQuestionLogic;
